using UnityEngine;using System.Collections;public class NavPath: MonoBehaviour {    public Transform target;    NavMeshAgent agent;    // Use this for initialization    void Start () {        agent = GetComponent<NavMeshAgent>();        NavMeshPath path = new NavMeshPath();        agent.CalculatePath(target.position, path);        if (path.status == NavMeshPathStatus.PathComplete) {            foreach (Vector3 corner in path.corners) {                Debug.Log(corner);            }        }    }		// Update is called once per frame	void Update () {            }}